{"linux":"// Place your key bindings in this file to override the defaults
[
  {
    "key": "ctrl+k",
    "command": "selectPrevSuggestion",
    "when": "suggestWidgetMultipleSuggestions && suggestWidgetVisible && textInputFocus"
  },
  {
    "key": "ctrl+j",
    "command": "selectNextSuggestion",
    "when": "suggestWidgetMultipleSuggestions && suggestWidgetVisible && textInputFocus"
  },
  { "key": "tab", "command": "acceptAlternativeSelectedSuggestion" },
  {
    "key": "ctrl+j",
    "command": "workbench.action.quickOpenSelectNext",
    "when": "inQuickOpen"
  },
  {
    "key": "ctrl+k",
    "command": "workbench.action.quickOpenSelectPrevious",
    "when": "inQuickOpen"
  },
  {
    "key": "tab",
    "command": "workbench.action.acceptSelectedQuickOpenItem",
    "when": "inQuickOpen"
  },
  {
    "key": "ctrl+shift+d ctrl+shift+f",
    "command": "redo"
  },
  {
    "key": "ctrl+y",
    "command": "-redo"
  },
  {
    "key": "ctrl+shift+q",
    "command": "fuzzySearch.activeTextEditor"
  },
  {
    "key": "ctrl+alt+w",
    "command": "workbench.action.closeEditorsInGroup"
  },
  {
    "key": "ctrl+k w",
    "command": "-workbench.action.closeEditorsInGroup"
  },
  {
    "key": "shift+alt+z",
    "command": "workbench.action.toggleZenMode"
  },
  {
    "key": "ctrl+k z",
    "command": "-workbench.action.toggleZenMode"
  },
  {
    "key": "ctrl+alt+w",
    "command": "editor.action.inlineSuggest.trigger",
    "when": "config.github.copilot.inlineSuggest.enable && editorTextFocus && !editorHasSelection && !inlineSuggestionsVisible"
  },
  {
    "key": "alt+\\",
    "command": "-editor.action.inlineSuggest.trigger",
    "when": "config.github.copilot.inlineSuggest.enable && editorTextFocus && !editorHasSelection && !inlineSuggestionsVisible"
  },
  {
    "key": "ctrl+alt+w",
    "command": "acceptSnippet"
  },
  {
    "key": "ctrl+alt+b",
    "command": "github.copilot.acceptPanelSolution"
  },
  {
    "key": "tab tab",
    "command": "editor.action.inlineSuggest.commit",
    "when": "inlineSuggestionHasIndentationLessThanTabSize && inlineSuggestionVisible && !editorTabMovesFocus"
  },
  {
    "key": "tab",
    "command": "-editor.action.inlineSuggest.commit",
    "when": "inlineSuggestionHasIndentationLessThanTabSize && inlineSuggestionVisible && !editorTabMovesFocus"
  },
  {
    "key": "ctrl+alt+s",
    "command": "workbench.action.toggleActivityBarVisibility"
  },
  {
    "key": "alt+k",
    "command": "workbench.action.nextEditorInGroup",
    "when": "editorTextFocus && vim.active"
  },
  {
    "key": "alt+j",
    "command": "workbench.action.previousEditor",
    "when": "editorTextFocus && vim.active"
  },
  {
    "key": "alt+h",
    "command": "workbench.action.navigateLeft",
    "when": "editorTextFocus && vim.active"
  },
  {
    "key": "alt+l",
    "command": "workbench.action.navigateRight",
    "when": "editorTextFocus && vim.active"
  },
  {
    "key": "alt+shift+j",
    "command": "editor.action.moveLinesDownAction",
    "when": "editorTextFocus && vim.active"
  },
  {
    "key": "alt+shift+k",
    "command": "editor.action.moveLinesUpAction",
    "when": "editorTextFocus && vim.active"
  },
  {
    "key": "alt+g alt+c",
    "command": "git.checkout"
  },
  {
    "key": "alt+a",
    "command": "workbench.action.decreaseViewSize"
  },
  {
    "key": "alt+d",
    "command": "workbench.action.increaseViewSize"
  },
  {
    "key": "alt+s",
    "command": "workbench.action.decreaseViewHeight"
  },
  {
    "key": "alt+w",
    "command": "workbench.action.increaseViewHeight"
  },
  {
    "key": "alt+u",
    "command": "workbench.action.splitEditorDown"
  },
  {
    "key": "ctrl+alt+y",
    "command": "editor.action.showHover",
    "when": "editorTextFocus"
  },
  {
    "key": "ctrl+k ctrl+i",
    "command": "-editor.action.showHover",
    "when": "editorTextFocus"
  },
  {
    "key": "alt+i",
    "command": "bookmarks.list"
  },
  {
    "key": "alt+w",
    "command": "bookmarks.listFromAllFiles"
  },
  {
    "key": "shift+alt+u",
    "command": "workbench.view.extension.favorites-explorer"
  },
  {
    "key": "ctrl+[",
    "command": "editor.action.peekDefinition",
    "when": "editorHasDefinitionProvider && editorTextFocus && !inReferenceSearchEditor && !isInEmbeddedEditor"
  },
  {
    "key": "ctrl+shift+f10",
    "command": "-editor.action.peekDefinition",
    "when": "editorHasDefinitionProvider && editorTextFocus && !inReferenceSearchEditor && !isInEmbeddedEditor"
  },
  {
    "key": "alt+y",
    "command": "extension.fuzzySearch"
  },
  {
    "key": "ctrl+k ctrl+f",
    "command": "-extension.fuzzySearch"
  }
]"}
